import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './index.css';

const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [activeCategory, setActiveCategory] = useState('Web Application');
  const [domain, setDomain] = useState('');
  const [categories, setCategories] = useState([]);
  const [commands, setCommands] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddCategory, setShowAddCategory] = useState(false);
  const [showAddCommand, setShowAddCommand] = useState(false);

  // Form states
  const [newCategory, setNewCategory] = useState({
    name: '',
    type: 'Web Application',
    description: ''
  });

  const [newCommand, setNewCommand] = useState({
    name: '',
    command: '',
    description: '',
    category: '',
    phase: 'Reconnaissance',
    tags: ''
  });

  const phases = ['Reconnaissance', 'Scanning', 'Enumeration', 'Exploitation', 'Post-Exploitation', 'Reporting'];
  const categoryTypes = ['Web Application', 'Internal Network', 'External Network'];

  useEffect(() => {
    fetchCategories();
  }, []);

  useEffect(() => {
    if (activeCategory) {
      fetchCommands();
    }
  }, [activeCategory]);

  const fetchCategories = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/categories`);
      setCategories(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch categories');
      setLoading(false);
    }
  };

  const fetchCommands = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/commands`);
      const filteredCommands = response.data.filter(cmd => 
        cmd.category && cmd.category.type === activeCategory
      );
      setCommands(filteredCommands);
    } catch (err) {
      setError('Failed to fetch commands');
    }
  };

  const generateCommands = async () => {
    if (!domain.trim()) {
      setError('Please enter a domain name');
      return;
    }

    try {
      const categoryId = categories.find(cat => cat.type === activeCategory)?._id;
      const response = await axios.post(`${API_BASE_URL}/commands/generate`, {
        domain: domain.trim(),
        categoryId
      });

      // Update commands with generated versions
      const updatedCommands = commands.map(cmd => {
        const generated = response.data.find(gen => gen._id === cmd._id);
        return generated || cmd;
      });
      setCommands(updatedCommands);
      setError('');
    } catch (err) {
      setError('Failed to generate commands');
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text).then(() => {
      // Could add a toast notification here
    });
  };

  const handleAddCategory = async () => {
    try {
      await axios.post(`${API_BASE_URL}/categories`, newCategory);
      setNewCategory({ name: '', type: 'Web Application', description: '' });
      setShowAddCategory(false);
      fetchCategories();
    } catch (err) {
      setError('Failed to add category');
    }
  };

  const handleAddCommand = async () => {
    try {
      const categoryId = categories.find(cat => cat.name === newCommand.category)?._id;
      const commandData = {
        ...newCommand,
        category: categoryId,
        tags: newCommand.tags.split(',').map(tag => tag.trim()).filter(Boolean)
      };

      await axios.post(`${API_BASE_URL}/commands`, commandData);
      setNewCommand({
        name: '',
        command: '',
        description: '',
        category: '',
        phase: 'Reconnaissance',
        tags: ''
      });
      setShowAddCommand(false);
      fetchCommands();
    } catch (err) {
      setError('Failed to add command');
    }
  };

  const groupedCommands = commands.reduce((acc, command) => {
    const phase = command.phase;
    if (!acc[phase]) {
      acc[phase] = [];
    }
    acc[phase].push(command);
    return acc;
  }, {});

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="container">
      <div className="sidebar">
        <h2>üõ°Ô∏è PenTest Commands</h2>
        <nav>
          <ul>
            {categoryTypes.map(type => (
              <li key={type}>
                <button
                  className={activeCategory === type ? 'active' : ''}
                  onClick={() => setActiveCategory(type)}
                >
                  {type}
                </button>
              </li>
            ))}
          </ul>
        </nav>
      </div>

      <div className="main-content">
        {error && <div className="error">{error}</div>}

        <div className="domain-input">
          <h3>Target Domain</h3>
          <div className="input-group">
            <input
              type="text"
              value={domain}
              onChange={(e) => setDomain(e.target.value)}
              placeholder="Enter domain name (e.g., example.com)"
            />
            <button 
              className="generate-btn"
              onClick={generateCommands}
              disabled={!domain.trim()}
            >
              Generate Commands
            </button>
          </div>
        </div>

        {phases.map(phase => {
          const phaseCommands = groupedCommands[phase] || [];
          if (phaseCommands.length === 0) return null;

          return (
            <div key={phase} className="phase-section">
              <h2 className="phase-title">{phase}</h2>
              <div className="commands-grid">
                {phaseCommands.map(command => (
                  <div key={command._id} className="command-card">
                    <div className="command-header">
                      <div className="command-name">{command.name}</div>
                      <button
                        className="copy-btn"
                        onClick={() => copyToClipboard(command.generatedCommand || command.command)}
                      >
                        Copy
                      </button>
                    </div>
                    <div className="command-description">{command.description}</div>
                    <div className="command-code">
                      {command.generatedCommand || command.command}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          );
        })}

        <div className="admin-section">
          <h3>‚öôÔ∏è Admin Panel</h3>
          <div className="admin-buttons">
            <button 
              className="admin-btn"
              onClick={() => setShowAddCategory(true)}
            >
              Add Category
            </button>
            <button 
              className="admin-btn"
              onClick={() => setShowAddCommand(true)}
            >
              Add Command
            </button>
          </div>
        </div>

        {/* Add Category Modal */}
        {showAddCategory && (
          <div className="modal">
            <div className="modal-content">
              <h3>Add New Category</h3>
              <div className="form-group">
                <label>Name:</label>
                <input
                  type="text"
                  value={newCategory.name}
                  onChange={(e) => setNewCategory({...newCategory, name: e.target.value})}
                />
              </div>
              <div className="form-group">
                <label>Type:</label>
                <select
                  value={newCategory.type}
                  onChange={(e) => setNewCategory({...newCategory, type: e.target.value})}
                >
                  {categoryTypes.map(type => (
                    <option key={type} value={type}>{type}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Description:</label>
                <textarea
                  value={newCategory.description}
                  onChange={(e) => setNewCategory({...newCategory, description: e.target.value})}
                />
              </div>
              <div className="form-buttons">
                <button className="btn-cancel" onClick={() => setShowAddCategory(false)}>
                  Cancel
                </button>
                <button className="generate-btn" onClick={handleAddCategory}>
                  Add Category
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Add Command Modal */}
        {showAddCommand && (
          <div className="modal">
            <div className="modal-content">
              <h3>Add New Command</h3>
              <div className="form-group">
                <label>Name:</label>
                <input
                  type="text"
                  value={newCommand.name}
                  onChange={(e) => setNewCommand({...newCommand, name: e.target.value})}
                />
              </div>
              <div className="form-group">
                <label>Command (use {domain} as placeholder):</label>
                <input
                  type="text"
                  value={newCommand.command}
                  onChange={(e) => setNewCommand({...newCommand, command: e.target.value})}
                  placeholder="nmap -sC -sV {domain}"
                />
              </div>
              <div className="form-group">
                <label>Description:</label>
                <textarea
                  value={newCommand.description}
                  onChange={(e) => setNewCommand({...newCommand, description: e.target.value})}
                />
              </div>
              <div className="form-group">
                <label>Category:</label>
                <select
                  value={newCommand.category}
                  onChange={(e) => setNewCommand({...newCommand, category: e.target.value})}
                >
                  <option value="">Select Category</option>
                  {categories.map(category => (
                    <option key={category._id} value={category.name}>{category.name}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Phase:</label>
                <select
                  value={newCommand.phase}
                  onChange={(e) => setNewCommand({...newCommand, phase: e.target.value})}
                >
                  {phases.map(phase => (
                    <option key={phase} value={phase}>{phase}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Tags (comma separated):</label>
                <input
                  type="text"
                  value={newCommand.tags}
                  onChange={(e) => setNewCommand({...newCommand, tags: e.target.value})}
                  placeholder="nmap, scanning, ports"
                />
              </div>
              <div className="form-buttons">
                <button className="btn-cancel" onClick={() => setShowAddCommand(false)}>
                  Cancel
                </button>
                <button className="generate-btn" onClick={handleAddCommand}>
                  Add Command
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
